def smallestsubset(arr, B):
    n = len(arr);
    max_int = 99999999
    ans = max_int
    left = 0
    sum1 = 0
    for i in range(0,n):
        if arr[i]>B:
            return 1
        sum1 += arr[i]
        while sum1 >= B:
            ans = min(ans, i + 1 - left)
            left+=1
            sum1 -= arr[left]
    if ans == max_int:
        ans=-1
    return ans

